        //*******************************************************************************
        //excel dosya açma 1//CFX96 P210 V1.6
        void openexcelfile1()
        {
            openFileDialog1.Title = "Please, Select The Ct Result File";
            openFileDialog1.Filter = " Result File(*Ct*.*)|*Ct*.*";

            if (openFileDialog1.ShowDialog() == System.Windows.Forms.DialogResult.OK)
            {
                dosyaisimayarı();
                
                ExcelProje = ExcelUygulama.Workbooks.Open(dosya_yolu);
                ExcelProje.Save();
                //ExcelProje.SaveAs(dosya_yolu, excel.XlFileFormat.xlWorkbookDefault, Missing, Missing, false, Missing, excel.XlSaveAsAccessMode.xlNoChange);
                ExcelProje.Close(true, Missing, Missing);

                acma = 1;       //dosya seçildiğini gösteriyor.                
                //***********************************************************************
                //excelden tablo halinda bilgi alma
                baglanti = new OleDbConnection("Provider=Microsoft.ACE.OLEDB.12.0;Data Source=" + dosya_yolu + "; Extended Properties=Excel 12.0");
                //baglanti = new OleDbConnection("Provider=Microsoft.ACE.OLEDB.14.0;Data Source=" + dosya_yolu + "; Extended Properties=Excel 14.0");

                baglanti.Open();
                OleDbDataAdapter data_adaptor_well = new OleDbDataAdapter("select [Well] from [0$] ", baglanti);
                OleDbDataAdapter data_adaptor_fluor = new OleDbDataAdapter("select [Fluor] from [0$] ", baglanti);
                OleDbDataAdapter data_adaptor_content = new OleDbDataAdapter("select [Content] from [0$] ", baglanti);
                OleDbDataAdapter data_adaptor_sample = new OleDbDataAdapter("select [Sample] from [0$] ", baglanti);
                OleDbDataAdapter data_adaptor_ct = new OleDbDataAdapter("select [C(t) Mean] from [0$] ", baglanti);
                OleDbDataAdapter data_adaptor_sq = new OleDbDataAdapter("select [SQ Mean] from [0$] ", baglanti);
                baglanti.Close();

                dtwell = new DataTable();
                data_adaptor_well.Fill(dtwell);

                dtfluor = new DataTable();
                data_adaptor_fluor.Fill(dtfluor);

                dtcontent = new DataTable();
                data_adaptor_content.Fill(dtcontent);

                dtsample = new DataTable();
                data_adaptor_sample.Fill(dtsample);

                dtct = new DataTable();
                data_adaptor_ct.Fill(dtct);

                dtsq = new DataTable();
                data_adaptor_sq.Fill(dtsq);

                boyasayısı = (dtwell.Rows.Count);
                boyasayısı = boyasayısı + 2;        //VIC olmadığı için

                if (boyasayısı <= 0)
                {
                    MessageBox.Show("Wrong Product or Wrong Result File");
                }
                else
                {
                    //tamamlama1FAM();

                    temizle();        //ürün ilk seçildiğinde temizleme yapma.

                    calibratorbul1FAM();    //calibrator var mı?

                    if (bulindex == -1)
                    {
                        MessageBox.Show("Calibrator not found");
                    }
                    else
                    {
                        calibratorbul1VIC();
                        calibratorgir1();

                        if (kitcal0 == 0)
                        {

                        }
                        else
                        {
                            openexcelfile1d();
                        }
                    }
                }
            }
        }
