            ntcfamsq = "";
            ntcvicsq = "";
            gecici = sample.Length;         //sample dizinin toplam sayısını al.
            gecici = gecici + 2;            //eksik olan vic standart için 2 kuyucuk ekle
            gecici = gecici / 2;            //fam boyanın bittiği yeri bul.

