            {
                if (Math.Ceiling(Convert.ToDouble(dtFAM.Rows[i][kuyug].ToString())) <= 0)
                {
                    (dtFAM.Rows[i][kuyug]) = 0;
                }
                if (Math.Ceiling(Convert.ToDouble(dtFAM.Rows[i][kuyug].ToString())) > max)
                {
                    max = Math.Ceiling(Convert.ToDouble(dtFAM.Rows[i][kuyug].ToString()));
                }
            }
            if (max >= 70)
            {
                for (int i = 0; i < sayıeksenx; i++)
                {
