        //*******************************************************************************
        //tabloda tıklama
        private void sonuc_view_CellClick(object sender, DataGridViewCellEventArgs e)
        {
            tablorenk();
            sonucindex = e.RowIndex;
            if (sonucindex < kuyutur & sonucindex >= 0)
            {
                if (e.ColumnIndex == 0 && e.RowIndex > -1) // checkBox olan kolon mı tıklandı ? Bende ColumnIndex = 0
                {
                    bool eskiDeger = (bool)(sonuc_view.Rows[e.RowIndex].Cells[0].FormattedValue); // eski değeri al
                    bool yenideger = !eskiDeger; // değer değiştir, true ise false , false ise true yap

                    sonuc_view.Rows[e.RowIndex].Cells[e.ColumnIndex].Value = yenideger; // gridi güncelle: seçili ise kaldır, yoksa seç
                }

                kuyug = sonuc_view.Rows[sonucindex].Cells[1].Value.ToString();
                grafikciz();
            }
        }
        //*******************************************************************************
        //tabloda tıklanan kuyucuğa göre satırı seçme
        void tablosec()
        {
            gecici = 0;
            gecici1 = 0;

            for (int i = 0; i < sonuc_view.Rows.Count; i++)
            {
                if (kuyug == sonuc_view.Rows[i].Cells[1].Value.ToString())
                {
                    gecici1 = 1;
                    gecici = i;
                    break;
                }
            }
            if (gecici1 == 1)
            {
                for (int i = 0; i < sonuc_view.Rows.Count; i++)
                {
                    sonuc_view.Rows[i].Selected = false;            //tablo satır seçimlerini temizliyor.
                }

                sonuc_view.Rows[gecici].Selected = true;            //tablo satırı komple seçiyor.
                sonuc_view.FirstDisplayedScrollingRowIndex = gecici;//tablo seçilen satırı kaydırarak görünür hale getiriyor.
            }
        }
        //*******************************************************************************
        //pozitif kontrol işaretleme
        void tablopoz7()
        {
            gecici = sonuc_view.Rows.Count;
            gecici = gecici - 1;

            for (int i = 0; i < gecici; i++)
            {
                if (sonuc_view.Rows[i].Cells[3].Value.ToString() == "Pos Ctrl")
                {
                    MessageBox.Show("pos bulundu");
                    sonuc_view.Rows[i].Cells[7].Style.BackColor = Color.Brown;
                }
                else
                {
                }
            }
        }
