        //*******************************************************************************
        //hangi boyalar çizilecek
        void grafikciz()
        {
            adet.Visible = true;
            foreach (var series in adet.Series)
            {
                series.Points.Clear();
            }

            if (CH1.Checked == true)
            {
                if (bulFAM >= 1)
                {
                    boyas = boyasFAM;
                    boyac = "FAM";
                    grafikcizFAM();
                }

                if (bulSYBR >= 1)
                {
                    boyas = boyasSYBR;
                    boyac = "SYBR";
                    grafikcizSYBR();
                }
            }

            if (CH2.Checked == true)
            {
                if (bulHEX >= 1)
                {
                    boyas = boyasHEX;
                    boyac = "HEX";
                    grafikcizHEX();
                }

                if (bulTET >= 1)
                {
                    boyas = boyasTET;
                    boyac = "TET";
                    grafikcizQ670();
                }

                if (bulCAL540 >= 1)
                {
                    boyas = boyasCAL540;
                    boyac = "Cal Gold 540";
                    grafikcizCAL540();
                }

                if (bulVIC >= 1)
                {
                    boyas = boyasVIC;
                    boyac = "VIC";
                    grafikcizVIC();
                }
            }

            if (CH3.Checked == true)
            {
                if (bulROX >= 1)
                {
                    boyas = boyasROX;
                    boyac = "ROX";
                    grafikcizROX();
                }

                if (bulTEXAS >= 1)
                {
                    boyas = boyasTEXAS;
                    boyac = "Texas Red";
                    grafikcizTEXAS();
                }

                if (bulCAL610 >= 1)
                {
                    boyas = boyasCAL610;
                    boyac = "Cal Red 610";
                    grafikcizCAL610();
                }
            }

            if (CH4.Checked == true)
            {
                if (bulCY5 >= 1)
                {
                    boyas = boyasCY5;
                    boyac = "CY5";
                    grafikcizCY5();
                }

                if (bulQ670 >= 1)
                {
                    boyas = boyasQ670;
                    boyac = "Quasar 670";
                    grafikcizQ670();
                }
            }

            if (CH5.Checked == true)
            {

                if (bulQ705 >= 1)
                {
                    boyas = boyasQ705;
                    boyac = "Quasar 705";
                    grafikcizQ705();
                }
            }
        }
        //*******************************************************************************
        //grafik çizimi
        void grafikcizFAM()
        {
            bulyer = 0;
            double[] egri1 = new double[sayıeksenx];
            this.adet.Series[boyas].Points.AddXY(0, 0);
            for (int i = 0; i < sayıeksenx; i++)
            {
                egri1[i] = Math.Ceiling(Convert.ToDouble(dtFAM.Rows[i][kuyug].ToString()));
                this.adet.Series[boyas].Points.AddXY(grafikx[i], egri1[i]);
