        //*******************************************************************************
        public DataTable CreateTable(DataGridView dataGridview)
        {
            DataTable tbl = new DataTable();
            DataRow row;
            for (int i = 0; i < sonuc_view.Columns.Count; i++)
            {
                tbl.Columns.Add(sonuc_view.Columns[i].HeaderText, typeof(string));
            }
            for (int i = 0; i < sonuc_view.Rows.Count; i++)
            {
                row = tbl.NewRow();
                for (int j = 0; j < sonuc_view.Columns.Count; j++)
                {
                    row[sonuc_view.Columns[j].HeaderText] = sonuc_view.Rows[i].Cells[j].Value;
                }
                tbl.Rows.Add(row);
            }
            return tbl;
        }
